            left join AIRLINE_NAMES_MAPPING as ANM_OW on ANM_OW.NAME_ACASA_RAW=ACASA.GES_OWNER
            left join AIRLINE_NAMES_MAPPING as ANM_MA on ANM_MA.NAME_ACASA_RAW=ACASA.GES_MANAGER
