                )                                   as B            on B.KREDITNEHMEREINHEIT_SAP = ZO_KUNDE_A.KREDITNEHMEREINHEIT_SAP and ZO_KUNDE_A.CUTOFFDATE = B.CUTOFFDATE
            where CLIENT_DATA.KUNDENNUMMER is not NULL
        ) where ORDER_NUMBER = 1
    ),
    PRE_AA_REC as (
        select distinct
            KD_NR,
            BB.CUTOFFDATE,
            AIRLINE_CUSTOMER_NUMBER,
            BILANZSTICHTAG,
            WAEHRUNG as CURRENCY,
            EBITDA_9320,
            FLOAT(UMSATZERLOESE_4000_1) as TRANSACTION_VOLUME,
            Float(HAFTENDES_EIGENKAPITAL_INKL_AUSZUSCHUETTENDER_GEWINN_AR_2499) as CAPITAL_STOCK,
            case
                when COUNTRY = '001 FRANKREICH' then 'FR'
                when COUNTRY = '003 NIEDERLANDE' then 'NL'
                when COUNTRY = '005 ITALIEN' then 'IT'
                when COUNTRY = '018 LUXEMBURG' then 'LU'
                when COUNTRY = '028 NORWEGEN' then 'NO'
                when COUNTRY = '030 SCHWEDEN' then 'SE'
                when COUNTRY = '052 TÜRKEI' then 'TR'
                when COUNTRY = '106 GROSSBRITANNIEN' then 'GB'
                when COUNTRY = '334 ÄTHIOPIEN' then 'ET'
                when COUNTRY = '400 VEREINIGTE STAATEN VON AMERIKA' then 'US'
                when COUNTRY = '404 KANADA' then 'CA'
                when COUNTRY = '412 MEXIKO' then 'MX'
                when COUNTRY = '442 PANAMA' then 'PA'
                when COUNTRY = '480 KOLUMBIEN' then 'CO'
                when COUNTRY = '508 BRASILIEN' then 'BR'
                when COUNTRY = '512 CHILE' then 'CL'
                when COUNTRY = '624 ISRAEL' then 'IL'
                when COUNTRY = '647 VEREINIGTE ARABISCHE EMIRATE' then 'AE'
                when COUNTRY = '701 MALAYSIA' then 'MY'
                when COUNTRY = '720 CHINA' then 'CN'
                when COUNTRY = '728 SÜDKOREA' then 'KR'
                when COUNTRY = '004 DEUTSCHLAND' then 'DE'
                when COUNTRY = '075 RUSSLAND' then 'RU'
                when COUNTRY = '413 BERMUDA' then 'BM'
                when COUNTRY = '708 PHILIPPINEN' then 'PH'
                when COUNTRY = '007 IRLAND' then 'IE'
                else COUNTRY
            end as COUNTRY,
            row_number() over (partition by AIRLINE_CUSTOMER_NUMBER order by BILANZSTICHTAG desc nulls last) as NBR
        from (
                select * from AIRCRAFT_REC_DATA
                    union all
                select * from CLIENT_TO_AIRLINE
              ) as BB
        left join NLB.IWHS_KUNDE_CURRENT as CLIENT_DATA_IWHS on 'NLB_' || BORROWERID =  AIRLINE_CUSTOMER_NUMBER and BB.CUTOFFDATE= CLIENT_DATA_IWHS.CUTOFFDATE
        where CLIENT_DATA_IWHS.CUTOFFDATE is not NULL
    ),
    AIRCRAFT_PRE_FINISH as (
        select
            KD_NR
            ,CUTOFFDATE                                                          as CUT_OFF_DATE
            ,AIRLINE_CUSTOMER_NUMBER                                             as AIRLINE_ID
            , CURRENCY                                                           as AIRLINE_BALANCESHEET_CURRENCY_ISO
            ,EBITDA_9320                                                         as AIRLINE_BALANCESHEET_EBITDA
            ,BILANZSTICHTAG                                                      as AIRLINE_BALANCESHEET_DATE
            , TRANSACTION_VOLUME                                                 as AIRLINE_BALANCESHEET_UMSATZ
            ,COUNTRY                                                             as AIRLINE_COUNTRY
            , CAPITAL_STOCK                                                      as AIRLINE_BALANCESHEET_SHARE_CAPITAL
            ,ROW_NUMBER() over (PARTITION BY KD_NR order by BILANZSTICHTAG desc) as NBR
        from PRE_AA_REC as PAP where NBR = 1  -- row_number() over (partition by AIRLINE_CUSTOMER_NUMBER order by BILANZSTICHTAG desc nulls last)
    ),
    GUARANTOR_INFORMATION as (
        select DISTINCT CUTOFFDATE, KUNDENNUMMER, RECOURSE
        from NLB.MI_GUARANTOR_INFORMATION
        union all
        select DISTINCT CUTOFFDATE, KUNDENNUMMER, RECOURSE
        from BLB.MI_GUARANTOR_INFORMATION
        union all
        select DISTINCT CUTOFFDATE, KUNDENNUMMER, RECOURSE
        from ANL.MI_GUARANTOR_INFORMATION
        union all
        select distinct CUTOFFDATE, KD_NR, RECOURSE
        from AIRCRAFT_REC_BASIS
    ),
    AIRLINES_PRE as (
        select AIRCRAFT_PRE_FINISH.*,
               CASE
                   when lower(RECOURSE) = 'ja' then 1
                   when lower(RECOURSE) = 'nein' then 0
                   else RECOURSE
                end         as RECOURSE
        from AIRCRAFT_PRE_FINISH
        left join GUARANTOR_INFORMATION as RECC on RECC.KUNDENNUMMER = AIRCRAFT_PRE_FINISH.KD_NR and RECC.CUTOFFDATE = AIRCRAFT_PRE_FINISH.CUT_OFF_DATE
    ),
    -- Distinkte Fluggesellschaften + Informationen
    AIRLINES as (
        select distinct
            CUT_OFF_DATE                                    AS CUT_OFF_DATE,                -- Stichtag
            LEFT(KD_NR,3)                                   AS CLIENT_BRANCH,        -- Institut des Schuldners/Kreditnehmers
            SUBSTRING(KD_NR,5,LENGTH(KD_NR)-4)              AS CLIENT_ID,     -- Kundennummer des Schuldners/Kreditnehmers
            LEFT(AIRLINE_ID,3)                              AS AIRLINE_BRANCH,      -- Institut des Bürgen
            SUBSTRING(AIRLINE_ID,5,LENGTH(AIRLINE_ID)-4)    AS AIRLINE_CLIENT_ID,   -- Kundennummer des Bürgen
            AIRLINE_BALANCESHEET_CURRENCY_ISO               AS AIRLINE_BALANCESHEET_CURRENCY_ISO,   -- Währung der Geld-Felder
            AIRLINE_BALANCESHEET_EBITDA                     AS AIRLINE_BALANCESHEET_EBITDA,         -- EBITDA
            AIRLINE_BALANCESHEET_DATE                       AS AIRLINE_BALANCESHEET_DATE,           -- Bilanzstichtag
            AIRLINE_BALANCESHEET_UMSATZ                     AS AIRLINE_BALANCESHEET_TURNOVER_TOTAL, -- Gesammtumsatz
            AIRLINE_COUNTRY                                 AS AIRLINE_BALANCESHEET_COUNTRY_ALPHA2, -- 2-stelliger Ländercode
            AIRLINE_BALANCESHEET_SHARE_CAPITAL              AS AIRLINE_BALANCESHEET_SHARE_CAPITAL,  -- Stammkapital
            RECOURSE                                        AS RECOURSE,                    -- Risikorückbehalt?
            NULL                                            AS RECOURSE_REASON,             -- N.A. (Grund für Risikorückbehalt)
            NBR                                             AS ORDER_NUMBER,                -- Nummer um Einträge (nach Wichtigkeit?) zu sortieren
            'AIRLINE'                                       AS ROLE,                        -- Dies ist eine Fluggesellschaft
            NULL                                            AS COMMENT                      -- N.A. (Kommentar)
        from AIRLINES_PRE
    )
select * from AIRLINES;
